	('04dcba6d-fdec-41b0-8c24-f0762baf4d62', 'Footer_Vtech', '<p>Footer_Vtech<br></p>', '3ce941c4-b9f0-4ebf-95d4-1d4a411d7ce5', '2018-06-11 14:53:22', NULL, 'footer', NULL, '', '<div class="footer-container">\n    <div class="nous-suivre">\n    	<div class="container">\n    		<div class="hb-widget hb-widget-text" data-field="footer-button" data-caption="text: Text">\n    			<span class="go-up" style="display: block;" >{{footer-button.text}}</span>\n    		</div>\n    		<div class="container-nous-suivre">\n	            <div class="nous-suivre-title hb-widget hb-widget-text" data-field="footer-title" data-caption="text: Text" ><span>{{{title-footer.text}}}</span></div>\n		            <div class="nous-suivre-links hb-widget hb-widget-image hb-widget-list" data-field="footer-list-suivre">\n		                <div class="nous-suivre-links-list">\n		                    <div class="row">\n		                        <div class="col-xs-12 col-md-10">\n		                            {{#foreach list-suivre}}\n		                            <div class="nous-suivre-col col-md-3 col-xs-6">\n		                                <div class="nous-suivre-row {{url_title}}">\n		                                	<img class="img-responsive resize icon" data-src="{{asset "{{file}}"}}">\n		                                    <a href="{{url}}" title="{{url_title}}">\n			                                    <p class="nous-suivre-text">{{{text}}}</p>\n			                                </a>\n		                            	</div>\n			                        </div>\n			                        {{/foreach}}\n		                	 	</div>\n	                    		<div class="col-xs-12 col-md-2">\n		                    </div>\n		                </div>\n		            </div>\n		        </div>\n		    </div>\n    	</div>\n    </div>\n    <div class="footer">\n	    	<div class="container">\n	    		<div class="footer-link-list-container hb-widget hb-widget-menu" data-field="footer-menu">\n	            <div class="menus