g(elem);\n	if (!elem.length) {\n		return;\n	}\n	var w = $(window).width();\n	//if on mobile then set new offset\n	if(w<=550){\n		if($(window).scrollTop()<=100){\n			var offset = elem[0].offsetTop - ($hashMenu.outerHeight(true)*2 + 65);\n		}else{\n	    	var offset = elem[0].offsetTop - ($hashMenu.outerHeight(true)*2 - 35);\n		}\n	}else{\n		//if menuhashtag not fixed in top then set new offset.\n		if($(window).scrollTop()<=265){\n			var offset = elem[0].offsetTop - ($hashMenu.outerHeight(true)*2 + 40);\n		}else{\n	    	var offset = elem[0].offsetTop - $hashMenu.outerHeight(true)*2;\n		}\n	}\n	// console.log(\'elem[0].offsetTop : \'+elem[0].offsetTop);\n	// console.log(\'$(window).scrollTop  : \'+$(window).scrollTop());\n	// console.log(\'offset  : \'+offset);\n	\n	$(\'html, body\').animate({\n        scrollTop: offset\n    }, 550);\n\n}\n\n$hashMenu.find(\'.menu li a\').click(function(e){\n	// e.preventDefault();\n    var $el = $(this);\n    var url = $el.attr(\'href\');\n    var title = $el.attr(\'title\');\n    var modal = $el.data(\'targer\');\n    if (url) {\n    	return window.open(url, \'blank\');\n    }\n    if (title) {\n    	return smooth_scroll_to($(title));\n    }\n});\n$hashMenuMobile.find(\'.list-unstyled li a\').click(function(e){\n	// e.preventDefault();\n    var $el = $(this);\n    if ($el.attr(\'title\') != "") {\n	    $root.find(\'.menu-mobile\').removeClass(\'active\');\n	    var url = $el.attr(\'href\');\n	    var title = $el.attr(\'title\');\n	    var modal = $el.data(\'targer\');\n	    if (url) {\n	    	return window.open(url, \'blank\');\n	    }\n	    if (title) {\n	    	return smooth_scroll_to($(title));\n	    }\n    }\n\n});\n\n\n\n$hashMenuMobile.find(\'.list-unstyled li a\').click(function(e){\n	var $el = $(this);\n	// $el.parents(\'li\').children(\'.child-menu\').css(\'display\',\'block\');\n	$el.parents(\'li\').children(\'.child-menu\').toggle();\n})\n	\n$window.scroll(function(){\n	\n	if ($hashMenu.not(\'.sticky\').length){\n		offset_Menu = $hashMenu.not(\'.sticky\').offset().top;\n	}\n	var fromTop = $window.scrollTop() + $hashMenu.outerHeight(true)*2 ;\n	if (fromTop > offset_Menu) {\n		$hashMenu.addClass(\'sticky\');\n	} else {\n		$hashMenu.removeClass(\'sticky\');\n	}\n	\n	// Get id of current scroll item\n	var cur = $scrollItems.map(function(){\n        if ($(this).offset().top < fromTop + 70)\n            return this;\n    });\n    cur = cur[cur.length-1];\n   	var id = cur && cur.length ? cur[0].id : "";\n    if (id != currId) {\n		currId = id;\n		$hashMenu.find(\'li\').removeClass(\'active\');\n		$hashMenu.find(\'a[title="#\' + currId + \'"]\').parent().addClass(\'active\');\n    }\n   \n});\n\n$root.find(\'.modal\').on(\'shown.bs.modal\', function () {\n  $(\'.modal-backdrop\').css(\'position\',\'static\')\n  $root.find(\'.menu-list-links\').addClass("visible");\n  $root.find(\'.menu-list-links li\').addClass("hidden");\n})\n$root.find(\'.modal\').on(\'hide.bs.modal\', function () {\n  $root.find(\'.menu-list-links\').removeClass("visible");\n  $root.find(\'.menu-list-links li\').removeClass("hidden");\n})\n\n\n\n\n$root.find(\'.button-show-sibar\').on(\'click\',function(){\n	$root.find(\'.menu-mobile\').addClass(\'active\');\n});\n$root.find(\'.button-close-sibar\').on(\'click\',function(){\n	$root.find(\'.menu-mobile\').removeClass(\'active\');\n})\n\n\n\n	', NULL, NULL, NULL, 0, '{"variables":[{"name":"@menu-sticky-background-color","desc":"","value":"#1375c0","lov":""},{"name":"@menu-item-background-color","desc":"","value":"#1375c0","lov":""},{"name":"@menu-item-font-size","desc":"","value":"11px","lov":""},{"name":"@menu-sub-item-background-color","desc":"","value":"#1375c0","lov":""},{"name":"@menu-item-background-color-hover","desc":"","value":"#013e7d","lov":""},{"name":"@menu-sub-item-background-color-hover","desc":"","value":"#013e7d","lov":""},{"name":"@menu-sub-item-text-color","desc":"","value":"#ffffff","lov":""},{"name":"@menu-item-text-color","desc":"","value":"#ffffff","lov"