	('d29f4f7e-6c81-4f93-db6f-fd5316fc2090', 'Full width background image slider', NULL, '3ce941c4-b9f0-4ebf-95d4-1d4a411d7ce5', '2018-03-22 15:50:12', NULL, 'widget', NULL, '', '<link rel="stylesheet" type="text/css" href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.8.1/slick.css" />\n<link rel="stylesheet" type="text/css" href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.8.1/slick-theme.min.css" />\n<script type="text/javascript" src="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.8.1/slick.min.js"></script>\n\n<div class="slider slider-single hb-widget hb-widget-image hb-widget-list" data-field="imagesSlider" data-caption="file:Image, url:Url, url_title: Url_Title">\n    {{#foreach imagesSlider}}\n    <a href="{{url}}">\n        <div class="wrap-img">\n            <img class="image img img-responsive resize" data-src="{{asset " {{file}} "}}">\n        </div>\n    </a>\n    {{/foreach}}\n</div>\n<div class="slider slider-nav">\n    {{#foreach imagesSlider}}\n    <div class="title-banner-wrapper">\n        <a><span class="title-banner">{{url_title}}</span></a>\n    </div>    \n    {{/foreach}}\n</div>', '.slick-item {\n    overflow: hidden;\n    position: relative;\n    .wrap-img {\n        margin: 0 auto;\n        .image {\n            max-width: 100%;\n        }\n    }\n    .text {\n        font-size: 1em;\n        font-weight: normal;\n        * {\n            font-family: cantoraoneregular, Arial;\n            text-decoration: none;\n            text-transform: uppercase;\n            font-size: 14px;\n            margin: 0;\n            color: #1375C0;\n            text-align: center;\n        }\n    }\n}\n\n.slick-arrow {\n	display: none !important;\n}\n.slider-nav .slick-list {\n	overflow: initial !important;\n	@media(max-width:767px){\n		display: none;\n	}\n}\nspan.title-banner {\n	font-size: 11px;\n    line-height: 12px;\n    font-family: "Opens Sans",Arial;\n    font-weight: 600;\n    text-transform: uppercase;\n    font-family: \'cantoraoneregular\', Arial;\n    text-decoration: none;\n    color: #ffffff;\n    padding: 0 10px;\n}\n\n.title-banner-wrapper {\n	background-color: #1375c0;\n	border-right: 1px solid #fff;\n	height: 46px;\n	text-align: center;\n	cursor: pointer;\n	display: table;\n	a {\n		display: table-cell;\n    vertical-align: middle;\n	}\n	&:hover {\n		background-color: #013e7d;\n	}\n    &:first-child {\n    	border-radius: 0 0 0 10px;\n    }\n    &:last-child {\n    	border-radius: 0 0 10px 0;\n    	border-right: none;\n    }\n    &:focus {\n    	outline: none !important;\n    }\n    &.is-active {\n    	background-color: #013e7d;\n    	position: relative;\n    	&:before {\n		    content: "";\n		    width: 0px;\n		    height: 0px;\n		    border-style: solid;\n		    border-width: 0px 13px 12px;\n		    border-color: transparent transparent #013F7D;\n		    position: absolute;\n		    top: -12px;\n		    left: 50%;\n		    margin-left: -12px;\n    	}\n    }\n}\n\n> .container {\n    width: 100%;\n    padding: 0;\n}\n\n.arrow-banner {\n    position: absolute;\n    z-index: 100;\n    color: #1375C0;\n    top: 50%;\n    &.arrow-left {\n        left: 0;\n    }\n    &.arrow-right {\n        right: 0;\n    }\n}\n\n/*ul.slick-dots {*/\n/*    display: none !important;*/\n/*}*/\n\n.item-cat {\n    text-align: left;\n    background-color: #fff;\n    margin-bottom: 26px;\n    font-size: 25px;\n    overflow: hidden;\n    margin: 20px 0;\n    font-family: cantoraoneregular, Arial;\n    font-weight: normal;\n    text-transform: uppercase;\n    color: #1375C0;\n    &:after {\n        content: "";\n        display: inline-block;\n        height: 14px;\n        vertical-align: bottom;\n        width: 100%;\n        margin-right: -100%;\n        margin-left: 10px;\n        border-top: 2px solid #1375C0;\n    }\n}\n', '$root.find(\'.wrap-slider\').slick({\r\n    fade: true,\r\n    infinite: true,\r\n    slidesToShow: 1,\r\n    slidesToScroll: 1,\r\n    autoplay: true,\r\n    autoplaySpeed: 2000\r\n});\r\n\r\n$(\'.slider-single\').slick({\r\n    slidesToShow: 1,\r\n    slidesToScroll: 1,\r\n    arrows: false,\r\n    fade: true,\r\n    adaptiveHeight: true,\r\n    autoplay: true,\r\n    useTransform: true,\r\n    speed: 400,\r\n    cssEase: \'cubic-bezier(0.77, 0, 0.18, 1)\',\r\n});\r\n\r\n$(\'.slider-nav\')\r\n    .on(\'init\', function(event, slick) {\r\n        $(\'.slider-nav .slick-slide.slick-current\').addClass(\'is-active\');\r\n    })\r\n    .slick({\r\n        slidesToShow: 7,\r\n        slidesToScroll: 7,\r\n        dots: false,\r\n        focusOnSelect: false,\r\n        infinite: false,\r\n        responsive: [{\r\n            breakpoint: 1024,\r\n            settings: {\r\n                slidesToShow: 5,\r\n                slidesToScroll: 5,\r\n            }\r\n        }, {\r\n            breakpoint: 640,\r\n            settings: {\r\n                slidesToShow: 4,\r\n                slidesToScroll: 4,\r\n            }\r\n        }, {\r\n            breakpoint: 420,\r\n            settings: {\r\n                slidesToShow: 3,\r\n                slidesToScroll: 3,\r\n            }\r\n        }]\r\n    });\r\n\r\n$(\'.slider-single\').on(\'afterChange\', function(event, slick, currentSlide) {\r\n    $(\'.slider-nav\').slick(\'slickGoTo\', currentSlide);\r\n    var currrentNavSlideElem = \'.slider-nav .slick-slide[data-slick-index="\' + currentSlide + \'"]\';\r\n    $(\'.slider-nav .slick-slide.is-active\').removeClass(\'is-active\');\r\n    $(currrentNavSlideElem).addClass(\'is-active\');\r\n});\r\n\r\n$(\'.slider-nav\').on(\'click\', \'.slick-slide\', function(event) {\r\n    event.preventDefault();\r\n\r\n    var goToSingleSlide = $(this).data(\'slick-index\');\r\n\r\n    $(\'.slider-single\').slick(\'slickGoTo\', goToSingleSlide);\r\n});', NULL, NULL, NULL, 0, NULL, NULL, NULL, NULL, NULL, NULL),
